import React from "react";
import { PlusOutlined, LockOutlined } from '@ant-design/icons';
import { Radio, Upload, Form, Input, Row, Col } from "antd";
export default function UserInfo() {
  const layout = {
    labelCol: { span: 8 },
    wrapperCol: { span: 16 },
  };
  return (
    <>
    <div className="space30"></div>
    <Row gutter={[24, 24]}>
      <Col className="gutter-row" span={12}>
        <div className="whiteBox shadow">
          <div className="pad20">
            <h3 style={{ color: "#22075e", marginBottom: 5 }}>
              KHÓA HỌC MỚI
            </h3>
          </div>
          <RecentTable entity={"NEW_COURSES"} dataTableColumns={postColumns} config={config} />
        </div>
      </Col>

      <Col className="gutter-row" span={12}>
        <div className="whiteBox shadow">
          <div className="pad20">
            <h3 style={{ color: "#22075e", marginBottom: 5 }}>
              ĐỀ THI MỚI
            </h3>
          </div>
          <RecentTable entity={"NEW_EXAMS"} dataTableColumns={postColumns} config={config} />
        </div>
      </Col>
    </Row>
    </>
  );
}
