import React from "react";
import { PlusOutlined, LockOutlined } from '@ant-design/icons';
import { Radio, Upload, Form, Input, Row, Col } from "antd";
import { CrudLayout } from "@/layout";
export default function UserInfo() {
  const layout = {
    labelCol: { span: 8 },
    wrapperCol: { span: 16 },
  };
  return (
    <CrudLayout>
    <div className="strong whiteBox" style={{ textAlign: "center", justifyContent: "center", minHeight: "20px", padding: "20px" }}>
        <h2 style={{ color: "#22075e", marginBottom: 0 }}>Hồ sơ thành viên</h2>
      </div>
      <div className="space30"></div>
    <Row gutter={[24, 24]}>
      <Col className="gutter-row" span={8}>
        <div className="whiteBox shadow">
          <div className="pad20">
            <h3 style={{ color: "#22075e", marginBottom: 5 }}>
              KHÓA HỌC MỚI
            </h3>
          </div>
        </div>
      </Col>

      <Col className="gutter-row" span={16} style={{height: "500px"}}>
        <div className="whiteBox shadow">
          <div className="pad20">
            <h3 style={{ color: "#22075e", marginBottom: 5 }}>
              ĐỀ THI MỚI
            </h3>
          </div>
        </div>
      </Col>
    </Row>
    </CrudLayout>
  );
}
