import React from "react";
import { Form, Input, Select, Row, Col, DatePicker } from "antd";
const { RangePicker } = DatePicker;
export default function SortForm() {
  const valueSearchAll = {
    status: "active",
    keyword: "123123",
    startDate: "01-10-2022",
    endDate: "30-11-2022",
    categories: "TcJAgpBIu_5t6ljAi2M_1",
    page: 1,
  }
  const changeSelectCategories = (Value) => {
    valueSearchAll.categories = Value?.join("_");
  };
  const changeSelectStatus = (Value) => {
    console.log(Value);
  };
  const changeSelectDate = (Value) => {
    console.log(Value);
  };
  const onChange = (fieldsValue) => {
    console.log(fieldsValue)
  };
  const [form] = Form.useForm();
  return (
    <>
    <Form form={form}  onChange={onChange}> 
      <Row gutter={16}>
        <Col span={6}>
          <Select
            name="categories"
            mode="multiple"
            style={{ width: "100%" }}
            placeholder="Danh mục dữ liệu"
            optionLabelProp="label"
            onSelect={onChange}
          >
            <Option value="TcJAgpBIu" label="Giáo viên">
              <div className="demo-option-label-item">Giáo viên</div>
            </Option>
            <Option value="5t6ljAi2M" label="Học sinh">
              <div className="demo-option-label-item">Học sinh</div>
            </Option>
            <Option value="1" label="Quản lý">
              <div className="demo-option-label-item">Quản lý</div>
            </Option>
          </Select>
        </Col>
        <Col span={6}>
          <Select
            name="status"
            showSearch
            style={{ width: 200 }}
            placeholder="Trạng thái"
            optionFilterProp="children"
            onSelect={onChange}
          >
            <Option value="all">Tất cả trạng thái</Option>
            <Option value="active">Đang hoạt động</Option>
            <Option value="inactive">Bị khóa</Option>
          </Select>
        </Col>
        <Col span={6}>
          <RangePicker name="date"  onChange={changeSelectDate}/>
        </Col>
        <Col span={6}>
          <Input name="keyword" placeholder="Tìm kiếm"/>
        </Col>
      </Row>
      </Form>
      <div className="space30"></div>
    </>
  );
}
