import React from "react";

import { Button, Menu } from "antd";
import { EyeOutlined, EditOutlined, DeleteOutlined } from "@ant-design/icons";
import { useSelector, useDispatch } from "react-redux";
import { crud } from "@/redux/crud/actions";
import { selectItemById } from "@/redux/crud/selectors";
import { useCrudContext } from "@/context/crud";
import uniqueId from "@/utils/uinqueId";
import DataTable from "@/components/DataTable";
import history from "@/utils/history";

function AddNewItem({ config }) {
  const { ADD_NEW_ENTITY  } = config;
  const handelClick = () => {
    history.push("/user/add");
  };

  return (
    <Button onClick={handelClick} type="primary">
      {ADD_NEW_ENTITY}
    </Button>
  );
}
function DropDownRowMenu({ row }) {
  console.log(row)
  const dispatch = useDispatch();
  // const { crudContextAction } = useCrudContext();
  // const { panel, collapsedBox, modal, readBox, editBox } = crudContextAction;
  const Show = () => {
    // dispatch(crud.currentItem(item));
    // panel.open();
    // collapsedBox.open();
    // readBox.open();  
  };
  function Edit() {
    // dispatch(crud.currentAction("update", item));
    // editBox.open();
    // panel.open();
    // collapsedBox.open();
  }
  function Delete() {
    const entity = "users"
    dispatch(crud.delete(entity, row?.nhan_vien_id));
    dispatch(crud.list(entity));
  }
  return (
    <Menu style={{ width: 130 }}>
      <Menu.Item key={`${uniqueId()}`} icon={<EyeOutlined />} onClick={Show}>
        Show
      </Menu.Item>
      <Menu.Item key={`${uniqueId()}`} icon={<EditOutlined />} onClick={Edit}>
        Edit
      </Menu.Item>
      <Menu.Item
        key={`${uniqueId()}`}
        icon={<DeleteOutlined />}
        onClick={Delete}
      >
        Delete
      </Menu.Item>
    </Menu>
  );
}

export default function AddData({ config }) {
  return (
    <DataTable
      config={config}
      DropDownRowMenu={DropDownRowMenu}
      AddNewItem={AddNewItem}
    />
  );
}
