import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { crud } from "@/redux/crud/actions";
import { useCrudContext } from "@/context/crud";
import { selectCreatedItem } from "@/redux/crud/selectors";
import { formatDate } from "@/utils/helpers";
import { Button, Form } from "antd";
import Loading from "@/components/Loading";
import { Editor, EditorState } from "draft-js";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";


export default function CreateFormNew({ config, formElements }) {
  let { entity } = config;
  const dispatch = useDispatch();
  const { isLoading, isSuccess } = useSelector(selectCreatedItem);
  const { editorState, setEditorState } = useState(EditorState.createEmpty());
  const [form] = Form.useForm();
  const onSubmit = (fieldsValue) => {
    const today = new Date();
    const dateTime = '2022-10-06T21:41:43.000Z';
    fieldsValue.ngay_sinh = dateTime;
    fieldsValue.ngay_tao = dateTime;
    fieldsValue.ngay_sua = dateTime;
    console.log(fieldsValue);
    dispatch(crud.create(entity, fieldsValue));
  };
  const onEditorStateChange = (editorState) => {
    console.log(editorState);
    // setEditorState(editorState);
  };
  const focusEditor = () => {
    editor.current.focus();
  }

  const editor = React.useRef(null);
  // useEffect(() => {
  //   if (isSuccess) {
  //     dispatch(crud.resetAction("create"));
  //     dispatch(crud.list(entity));
  //   }
  // }, [isSuccess]);

  return (
    <Loading isLoading={isLoading}>
      <Form form={form} layout="vertical" onFinish={onSubmit}>
        {formElements}
        <div
          style={{ border: "1px solid black", minHeight: "6em", cursor: "text" }}
          onClick={focusEditor}
        >
        <Editor
          ref={editor}
          editorState={editorState}
          onChange={onEditorStateChange}
          placeholder="Write something!"
        />
      </div>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </Loading>
  );
}
